themeCSS:
.node rect, .node circle, .node ellipse, .node polygon, .node path { stroke-width: 2px; }
 .edgePath .path { stroke-width: 2px !important; }
.packetBlock { fill: #ffffff !important; stroke: #000000 !important; }
.packet rect, .packet polygon { fill: #ffffff !important; stroke: #000000 !important; }
.packet text, .packetLabel, .packetByte, .packetTitle { fill: #000000 !important; }
.node rect, .node polygon, .node ellipse { fill: white !important; }
